CREATE TABLE ipclive.wp_ipc_documents_api (
  id INT(11) NOT NULL AUTO_INCREMENT,
  dataID VARCHAR(50) NOT NULL,
  case_reference VARCHAR(10) NULL,
  Stage TINYINT(4) NULL,
  type VARCHAR(255) NULL,
  filter_1 VARCHAR(255) NULL,
  filter_2 VARCHAR(255) NULL,
  category VARCHAR(255) NULL,
  description TEXT NULL,
  size INT(11) NULL,
  mime VARCHAR(500) NULL,
  path TEXT NULL,
  status VARCHAR(50) NULL,
  date_published DATE NULL,
  deadline_date DATE NULL,
  personal_name VARCHAR(255) NULL,
  representative VARCHAR(255) NULL,
  who_from VARCHAR(45) NULL,
  doc_reference VARCHAR(45) NULL,
  author VARCHAR(45) NULL,
  details VARCHAR(45) NULL,
  last_modified VARCHAR(45) NULL,
  date_created VARCHAR(45) NULL,
  PRIMARY KEY (id));


